// ReactDOM.render(<Klode />, document.getElementById('root'))


function Scene() {
    const colorMap = useLoader(TextureLoader, 'texture.jpeg')
    return (
      <>
        <ambientLight intensity={0.5} />
        <directionalLight color="white" position={[0, 0, 3]} />
        <Stars radius={20} depth={50} count={5000} factor={5} saturation={0} fade speed={3}/>
        <mesh position={[-3, 0, 0]}>
            <sphereGeometry/>
            <meshStandardMaterial map={colorMap}/>
        </mesh>
      </>
    )
}

export default function Klode() {
    return (
        <Canvas style={{height: "100vh", width: "100vw"}}>  
            <Scene />
        </Canvas>
    )
}
