import React, { useRef, Suspense } from 'react'
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { TextureLoader } from 'three/src/loaders/TextureLoader.js'
import { Stars } from "@react-three/drei"
import mapboxgl from "mapbox/mapbox-gl-js"


function Earth() {

    // const successCallback = (position) => {
    //     console.log(position)
    //     const latitude = (position.coords.latitude).toString()
    //     const longitude = (position.coords.longitude).toString()
    // }

    // const errorCallback = (error) => {
    //     console.error(error)
    // }

    // function getCord() {
    //     navigator.geolocation.getCurrentPosition(successCallback, errorCallback);
    // }   
    // getCord()

    var position = []

    function main() {
        navigator.geolocation.getCurrentPosition(position => {
        let lat = position.coords.longitude;
        let lng = position.coords.latitude;
        position.insert(lat, lng)
        });
    }

    main();

    // (A) INSERT ACCESS TOKEN HERE!
    mapboxgl.accessToken = "pk.eyJ1IjoidG9hc3RlZGJyZWFkNDgiLCJhIjoiY2w4Mzh2aXE3MDNqODN3cDZ0b2NvN2FtZiJ9.ECoocvTOOyxsaOV-BSBYUg";

  // (B) GET GPS COORDINATES
    navigator.geolocation.getCurrentPosition(
      // (B1) DRAW MAP
      (pos) => {
        // THE MAP
        let map = new mapboxgl.Map({
          container: "map",
          style: "mapbox://styles/mapbox/streets-v11",
          center: [pos.coords.longitude, pos.coords.latitude],
          zoom: 13
        });
        // ADD A MARKER
        let marker = new mapboxgl.Marker()
          .setLngLat([pos.coords.longitude, pos.coords.latitude])
          .addTo(map);
        },

      // (B2) ON FAILING TO GET GPS COORDINATES
        (err) => { console.error(err); },

      // (B3) GPS OPTIONS
      {
        enableHighAccuracy: true,
        timeout: 5000,
        maximumAge: 0
      }
    );

    "https://api.mapbox.com/styles/v1/toastedbread48/cl839iz82007c14n55lu6gp4y/static/10.7527011,59.9188933,17/1280x1280?access_token=pk.eyJ1IjoidG9hc3RlZGJyZWFkNDgiLCJhIjoiY2w4Mzh2aXE3MDNqODN3cDZ0b2NvN2FtZiJ9.ECoocvTOOyxsaOV-BSBYUg"
    const texture_1 = useLoader(TextureLoader, map)
    const mesh = useRef()
    useFrame(() => {
    mesh.current.rotation.x = mesh.current.rotation.y += 0.003
    })



  return (
    <mesh ref={mesh} position={[-3, 0, 0]}   scale={1.9}>
      <sphereGeometry args={[1, 100]}/>
      <meshStandardMaterial map={texture_1} attach="material" />
    </mesh>
  )
}

export default function Klode() {
  return (
    <Canvas style={{height: "100vh", width: "100vw"}}>
      <ambientLight intensity={0.06} />
      <spotLight position={[-10, 1, 30]} angle={0.15} penumbra={1} />
      {/* <pointLight position={[-10, -10, -10]} /> */}
      <Suspense fallback={null}>
        <Stars radius={1} depth={50} count={8000} factor={5} saturation={1} fade speed={3}/>
        <Earth/>
      </Suspense>
    </Canvas>
  )
}
