import ThreeGlobe from 'three-globe';
import * as THREE from 'three';
import { useState } from "react"
import { Canvas } from "@react-three/fiber"
import { Stars } from "@react-three/drei"
import Earth from "./Earth.js"

export default function Klode() {
    const [activeMarkerId, setActiveMarkerId] = useState("me")

    const markers = [{ id: "me", lat: 0, lon: 0}]
    const activeMarker = markers.find(marker => marker.id === activeMarkerId)

    console.log("active marker", activeMarker)

    return (
        <div>
            <Canvas style={{
                height: "100vh",
                width: "100vw",
            }}>
              <ambientLight intensity={0.1} />
                <directionalLight color="red" position={[0, 0, 5]} />
            {/* <pointLight position={[10,5,10]}/> */}
                <Stars radius={30} depth={50} count={5000} factor={5} saturation={0} fade speed={2}/>
                <Earth/>
                <mesh>
                    <boxGeometry />
                    <meshStandardMaterial />
                </mesh>
            </Canvas>
            <div className="canvas-placeholder"></div>
        </div>
    )
}

ReactDOM.render(<Klode />, document.getElementById('root'))
