import React, { useRef, Suspense } from 'react'
import { Canvas, useFrame, useLoader } from '@react-three/fiber'
import { TextureLoader } from 'three/src/loaders/TextureLoader.js'
import { Stars } from "@react-three/drei"


function Earth() {

    // const successCallback = (position) => {
    //     console.log(position)
    //     const latitude = (position.coords.latitude).toString()
    //     const longitude = (position.coords.longitude).toString()
    // }

    // const errorCallback = (error) => {
    //     console.error(error)
    // }

    // function getCord() {
    //     navigator.geolocation.getCurrentPosition(successCallback, errorCallback);
    // }   
    // getCord()


    // navigator.geolocation.getCurrentPosition(position => {let lat = position.coords.longitude; let lng = position.coords.latitude;});

    function main() {
      if (navigator.geolocation){
        navigator.geolocation.getCurrentPosition(showPosition)
      }
    }

    function showPosition(position){
      console.log(position.coords.latitude)
      console.log(position.coords.longitude)
    }

    main()
    const [colorMap, displacementMap, aoMap] = useLoader(TextureLoader, [
      './earthmap.jpg',
      './disp_map.jpg',
      "./earthcloudmap.jpg",
    ])

    // const texture_1 = useLoader(TextureLoader, "")
    const mesh = useRef()
    useFrame(() => {
    mesh.current.rotation.x = mesh.current.rotation.y += 0.003
    })



  return (
    <mesh ref={mesh} position={[-3, 0, 0]}   scale={1.9}>
      <sphereGeometry args={[1, 100, 1000]}/>
      <meshStandardMaterial map={colorMap} aoMap={aoMap} displacementMap={displacementMap} displacementScale={0.05} attach="material" />
    </mesh>
  )
}

export default function Klode() {
  return (
    <Canvas style={{height: "100vh", width: "100vw"}}>
      <ambientLight intensity={0.06} />
      <spotLight position={[-10, 1, 30]} angle={0.15} penumbra={1} />
      {/* <pointLight position={[-10, -10, -10]} /> */}
      <Suspense fallback={null}>
        <Stars radius={1} depth={50} count={8000} factor={5} saturation={1} fade speed={3}/>
        <Earth/>
      </Suspense>
    </Canvas>
  )
}
